import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, Navigate, useNavigate } from 'react-router-dom';
import HomePage from './components/HomePage';
import PlantDetailPage from './components/PlantDetailPage';
import plants from './data/plants'; // Import the plants array

const App = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [filteredPlants, setFilteredPlants] = useState(plants);
  const navigate = useNavigate();

  const handleSearch = (event) => {
    const query = event.target.value.toLowerCase();
    setSearchTerm(query);

    const results = plants.filter((plant) =>
      plant.name.toLowerCase().includes(query)
    );
    setFilteredPlants(results);

    // Navigate to the plant detail page if exactly one match is found
    if (results.length === 1) {
      navigate(`/plant/${results[0].id}`);
    }
  };

  return (
    <Router>
      <header>
        <h1>Virtual Herbal Garden</h1>
      </header>
      <nav>
        <Link to="/home">Home</Link>
      </nav>
      <main className="main-content">
        {/* Search Bar */}
        <div className="search-container">
          <input
            type="text"
            placeholder="Search plants..."
            value={searchTerm}
            onChange={handleSearch}
            className="search-bar"
          />
        </div>

        {/* Routes */}
        <Routes>
          <Route path="/" element={<Navigate replace to="/home" />} />
          <Route path="/home" element={<HomePage plants={filteredPlants} />} />
          <Route path="/plant/:id" element={<PlantDetailPage />} />
        </Routes>
      </main>
      <footer>
        <p>&copy; 2025 Virtual Herbal Garden</p>
      </footer>
    </Router>
  );
};

export default App;
